    [AllureNUnit]
    [Parallelizable]
    public class TestAllureAttachments:TestBase
    {
        String ProjectPath;

        [SetUp]
        public void localSetUp()
        {
            ProjectPath = ProjectUtil.GetProjectBaseDir();
        }



        [Test]
        public void TestAttachText1()
        {

            AllureApi.AddAttachment("data.txt", "text/plain", Encoding.UTF8.GetBytes("This is the file content."));
        
            string _imgFile = Path.Combine(ProjectPath, "Data-Dir/image8.png");
            string _imgFile1 = Path.Combine(ProjectPath, "Data-Dir/b64Image.png");
            Console.WriteLine(ProjectPath);
            AllureApi.AddAttachment("image1.png", "image/png", _imgFile);

            ProjectUtil.getImage(driver);

            ProjectUtil.takeSave(driver);
            // File.ReadAllBytes(TestUtil.ProjectUtil.getScreenShotAsBase64(driver,"file")));
            AllureApi.AddAttachment("file1.png", "image/png", _imgFile1);            // File.ReadAllBytes(TestUtil.ProjectUtil.getScreenShotAsBase64(driver,"file")));

            AllureApi.AddAttachment("file213.png", "image/png", Encoding.UTF8.GetBytes("data:image/png;base64,"));
            Allure.NUnit.Attachments.Text("", "image/png");


            AllureApi.AddAttachment("AttachByteArray.png", "image/png", ((ITakesScreenshot)driver).GetScreenshot().AsByteArray);
            AllureApi.AddAttachment("Json Example FIle", "application/json", ProjectPath+"Data-Dir/jsonExample.json");

        }




    }
}

